package hi;

public class ifelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//IF-ELSE
		int x = 20;
		int y = 20;
		
		if (x < y) {
			System.out.println("X is less than Y") ;
		} else if (x == y ) {
			System.out.println("X is equal to Y");
		} else {
			System.out.println("X is greater than Y"); 
		}	

	}

}
