package hi;

public class whilejava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//WHILE
		
		int x = -10;
		
		while (x <= 0) {
			System.out.println("x = "+ x);
			//3 ways of incrementing 
			//x++; and x = x + 1; 	x += 1;
			//x++;
			//x = x + 1;
			x += 1;
		}
	}
}